export default class GameObject {
    constructor(tag,layer) {
        this.tag = tag
    }

    destroy() {
        if(this.layer){
            this.layer.remove(this)
        }
    }

    render(ctx, elapse, interval) {
        if (this.sprite) {
            this.sprite.render(ctx, elapse, interval)
        }
    }
}