let game = new Game()
let backgroundImage=Game.Resource.load('background.png', 'background')
Game.Resource.load('monster-1.png', 'monster1')
game.createLayer('main')
game.Layers.main.setBackground(backgroundImage)
game.init(function (cur, all) {
    //alert(cur+','+all)
    console.log(cur,all)
    document.getElementById('progress').style.width = (cur / all * 100).toFixed(1) + '%';
    if (cur == all) {
        setTimeout(function () {
            document.querySelector('.progress-ctn').style.display = 'none'
        }, 300)

    }
}).start(function(){

})